package staticPrograms;

public class area {
	
	public area(int r2) {
		// TODO Auto-generated constructor stub
		double s=3.141*r2*r2;
		System.out.println("the area of circle is "+s);
	}
	public area(int l, int b) {
		// TODO Auto-generated constructor stub
		int s=l*b;
		System.out.println("the area of rectangle is "+s);
	}
	

}
